import React from 'react'
import {useFormik} from "formik";
import { registerValidationSchema } from '../../validate.js/validate';
import axios from "axios"

const registerData={username:"",email:"",password:""};



const Register = () => {
    const registerFormData=new FormData();
    const {values,errors,handleSubmit,handleChange,handleBlur}=useFormik({
        initialValues:registerData,
        validationSchema:registerValidationSchema,
        onSubmit:async(values,errors)=>{
            registerFormData.append("username",values.username);
            registerFormData.append("email",values.email);
            registerFormData.append("password",values.password);  

            await axios.post(`http://localhost:5000/user/register`,registerFormData).then((res)=>{
                console.log(res)
            }).catch((Err))


        }



    })
    return (
        <>
            <form encType='multipart/form-data'>
            <div class="mb-3">
                    <label for="exampleInputEmail1" class="form-label">Full name</label>
                    <input type="text" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" name="username"  onChange={handleChange} onBlur={handleBlur} value={values.username} />
                    <div id="emailHelp" class="form-text">We'll never share your email with anyone else.</div>
                </div>
                <div class="mb-3">
                    <label for="exampleInputEmail1" class="form-label">Email address</label>
                    <input type="email" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" name="email"  onChange={handleChange} onBlur={handleBlur} value={values.email} />
                    <div id="emailHelp" class="form-text">We'll never share your email with anyone else.</div>
                </div>
                <div class="mb-3">
                    <label for="exampleInputPassword1" class="form-label">Password</label>
                    <input type="password" class="form-control" id="exampleInputPassword1" name="password"  onChange={handleChange} onBlur={handleBlur} value={values.password}/>
                </div>
                <div class="mb-3 form-check">
                    <input type="checkbox" class="form-check-input" id="exampleCheck1" />
                    <label class="form-check-label" for="exampleCheck1">Check me out</label>
                </div>
                <input type="submit" value="submit"/>
            </form>
        </>
    )
}

export default Register
