import React from 'react'
import {useFormik} from "formik";
import { registerFormValidate } from '../../validate.js/validate';
const registerData={username:"",email:"",pa:""};


const Register = () => {
    const registerFormData=new FormData();
    const {values,errors,handleSubmit,handleChange,handleBlur}=useFormik({
        initialValues:registerData,
        validationSchema:registerFormValidate,
        onSubmit:async(values,errors)=>{
            registerFormData.append("username",values.username);
            registerFormData.append("email",values.email);
            registerFormData.append("password",values.password);  
        }



    })
    return (
        <>
            <form>
            <div class="mb-3">
                    <label for="exampleInputEmail1" class="form-label">Full name</label>
                    <input type="email" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" />
                    <div id="emailHelp" class="form-text">We'll never share your email with anyone else.</div>
                </div>
                <div class="mb-3">
                    <label for="exampleInputEmail1" class="form-label">Email address</label>
                    <input type="email" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" />
                    <div id="emailHelp" class="form-text">We'll never share your email with anyone else.</div>
                </div>
                <div class="mb-3">
                    <label for="exampleInputPassword1" class="form-label">Password</label>
                    <input type="password" class="form-control" id="exampleInputPassword1" />
                </div>
                <div class="mb-3 form-check">
                    <input type="checkbox" class="form-check-input" id="exampleCheck1" />
                    <label class="form-check-label" for="exampleCheck1">Check me out</label>
                </div>
                <button type="submit" class="btn btn-primary">Submit</button>
            </form>
        </>
    )
}

export default Register
