const initialState={
    user:localStorage.getItem("user")?JSON.parse(localStorage.getItem("user")):null,
    card:[]
}

const dataSlice=createSlice({
    name:"data",
    initialState,
    reducers:{
        login:(state,action)=>{
            state.user=action.payload
        },
        logout:(state,action)=>{
            localStorage.removeItem("user")
            state.user=null;
        },
        addCard:(state,action)=>{
