import { createSlice } from "@reduxjs/toolkit";


const initialState = {
    user: localStorage.getItem("user") ? JSON.parse(localStorage.getItem("user")) : null,
    card: []
}

const dataSlice = createSlice({
    name: "data",
    initialState,
    reducers: {
        login: (state, action) => {
            state.user = action.payload
        },
        logout: (state, action) => {
            localStorage.removeItem("user")
            state.user = null;
        },
        addCard: (state, action) => {
            state.card.push(action.payload)
        },
        set
    },
    extraReducers: (builder) => {
        // Disable resetting the state to initial state
        builder
            .addMatcher(
                (action) => action.type.startsWith('redux/reset'),
                (state) => state
            );
    },
})

export const { addCard, login, logout } = dataSlice.actions;
export default dataSlice.reducer



// thunk
export function fetchCard() {
    return async function fetchCardThunk() {
        await axios.get(`http://localhost:5000/post/getallpost/${state.user.user._id}`).then((res) => {
            console.log(res.data)
        }).catch((error) => {
            console.log(error)
        })
    }
}
