import React from 'react'
import {useFormik} from "formik";
import { registerValidationSchema } from '../../validate.js/validate';
import axios from "axios"

const registerData={username:"",email:"",password:""};



const Register = () => {
    const registerFormData=new FormData();
    const {values,errors,handleSubmit,handleChange,handleBlur}=useFormik({
        initialValues:registerData,
        validationSchema:registerValidationSchema,
        onSubmit:async(values,errors)=>{
            console.log("first")
            registerFormData.append("username",values.username);
            registerFormData.append("email",values.email);
            registerFormData.append("password",values.password);  

            await axios.post(`http://localhost:5000/user/register`,registerFormData).then((res)=>{
                console.log(res)
            }).catch((error)=>{
                console.log(error)
            })


        }



    })
    return (
        <>
            <form encType='multipart/form-data' onSubmit={handleSubmit}  method='POST'>
            <form  onSubmit={handleSubmit}  method='POST'>
                    <h1 className='title'>Register</h1>
                        <input type="text" name="username" id="username" required placeholder='fullname' value={values.username} onChange={handleChange} onBlur={handleBlur}/>
                        <input type="email" name="email" id="email" required placeholder='email'value={values.email} onChange={handleChange} onBlur={handleBlur} />
                        <input type="text" name="password" id="passowrd" required placeholder='paswword' value={values.password} onChange={handleChange} onBlur={handleBlur}/>
                        <div className="agree">
                            <input type="checkbox" name="" agree id="agree" />
                            <label for="agree">Yes,I agree to all Terms and Policy</label>
                        </div>
                        <input type="submit" value="Sign Up"/>

                        <p>Already Have An Account?<NavLink to="/login1" className="btn1">Login</NavLink></p>

                    </form>
                <input type="submit" value="submit"/>
            </form>
        </>
    )
}

export default Register
